mapper.select:
    
    select
        b.id, b.title, b.update_time, b.recommend, b.published, b.type_id, t.id, t.name
    from t_blog b join t_type t on b.type_id = t.id
        
            
              and b.type_id = #{typeId}
            
            
              and b.recommend = #{recommend}
            
            
              and b.title like #{pattern}
